half3 screen(half3 baseColor, half3 blendColor) {
    half3 resultColor;
    resultColor.rgb = 1.0 - ((1.0 - baseColor.rgb) * (1.0 - blendColor.rgb));

    return resultColor;
}

half3 screenVo( half3 s, half3 d )
{
    return s + d - s * d;
}
half3 darken( half3 s, half3 d )
{
    return min(s,d);
}

half3 multiply( half3 s, half3 d )
{
    return s*d;
}

half3 colorBurn( half3 s, half3 d )
{
    return 1.0 - (1.0 - d) / s;
}

half3 linearBurn( half3 s, half3 d )
{
    return s + d - 1.0;
}

half3 darkerColor( half3 s, half3 d )
{
    return (s.x + s.y + s.z < d.x + d.y + d.z) ? s : d;
}

half3 lighten( half3 s, half3 d )
{
    return max(s,d);
}

half3 colorDodge( half3 s, half3 d )
{
    return d / abs(1.0 - s);
}
float blendColorDodge(float base, float blend) {
    return (blend==1.0)?blend:min(base/(1.0-blend),1.0);
}

half3 colorDodge_n(half3 blend,half3 base) {
    return half3(blendColorDodge(base.r,blend.r),blendColorDodge(base.g,blend.g),blendColorDodge(base.b,blend.b));
}

half3 blendColorDodge(half3 base, half3 blend, float opacity) {
    return (colorDodge(base, blend) * opacity + base * (1.0 - opacity));
}
half3 linearDodge( half3 s, half3 d )
{
    return s + d;
}

half3 lighterColor( half3 s, half3 d )
{
    return (s.x + s.y + s.z > d.x + d.y + d.z) ? s : d;
}

float overlay( float s, float d )
{
    return (d < 0.5) ? 2.0 * s * d : 1.0 - 2.0 * (1.0 - s) * (1.0 - d);
}

