half3 overlay( half3 overlayColor,half3 baseColor) {
    half3 resultColor;
    resultColor.rgb = mix(
        2.0f * baseColor.rgb * overlayColor.rgb,
        1.0f - 2.0f * (1.0f - baseColor.rgb) * (1.0f - overlayColor.rgb),
        step(0.5f, baseColor.rgb)
    );
//    resultColor.a = baseColor.a; // keep the original alpha
    return resultColor;
}

float softLight( float s, float d )
{
    return (s < 0.5) ? d - (1.0 - 2.0 * s) * d * (1.0 - d)
        : (d < 0.25) ? d + (2.0 * s - 1.0) * d * ((16.0 * d - 12.0) * d + 3.0)
                     : d + (2.0 * s - 1.0) * (sqrt(d) - d);
}

half4 softLight( half4 s, half4 d )
{
    half4 c;
    c.x = softLight(s.x,d.x);
    c.y = softLight(s.y,d.y);
    c.z = softLight(s.z,d.z);
    c.a = softLight(s.a,d.a);
    return c;
}

float hardLight( float s, float d )
{
    return (s < 0.5) ? 2.0 * s * d : 1.0 - 2.0 * (1.0 - s) * (1.0 - d);
}

half3 hardLight( half3 s, half3 d )
{
    half3 c;
    c.x = hardLight(s.x,d.x);
    c.y = hardLight(s.y,d.y);
    c.z = hardLight(s.z,d.z);
    return c;
}

float vividLight( float s, float d )
{
    return (s < 0.5) ? 1.0 - (1.0 - d) / (2.0 * s) : d / (2.0 * (1.0 - s));
}

half3 vividLight( half3 s, half3 d )
{
    half3 c;
    c.x = vividLight(s.x,d.x);
    c.y = vividLight(s.y,d.y);
    c.z = vividLight(s.z,d.z);
    return c;
}

half3 linearLight( half3 s, half3 d )
{
    return 2.0 * s + d - 1.0;
}

float pinLight( float s, float d )
{
    return (2.0 * s - 1.0 > d) ? 2.0 * s - 1.0 : (s < 0.5 * d) ? 2.0 * s : d;
}

half3 pinLight( half3 s, half3 d )
{
    half3 c;
    c.x = pinLight(s.x,d.x);
    c.y = pinLight(s.y,d.y);
    c.z = pinLight(s.z,d.z);
    return c;
}

half3 hardMix( half3 s, half3 d )
{
    return floor(s + d);
}

half3 difference( half3 s, half3 d )
{
    return abs(d - s);
}

half3 exclusion( half3 s, half3 d )
{
    return s + d - 2.0 * s * d;
}

half3 subtract( half3 s, half3 d )
{
    return s - d;
}

half3 divideBlendMode( half3 s, half3 d )
{
    return s / d;
}


half3 hue( half3 s, half3 d )
{
    d = rgb2hsv(d);
    d.x = rgb2hsv(s).x;
    return hsv2rgb(d);
}

half3 color( half3 s, half3 d )
{
    s = rgb2hsv(s);
    s.z = rgb2hsv(d).z;
    return hsv2rgb(s);
}

half3 saturation( half3 s, half3 d )
{
    d = rgb2hsv(d);
    d.y = rgb2hsv(s).y;
    return hsv2rgb(d);
}

half3 luminosity( half3 s, half3 d )
{
    float dLum = dot(d, half3(0.3, 0.59, 0.11));
    float sLum = dot(s, half3(0.3, 0.59, 0.11));
    float lum = sLum - dLum;
    half3 c = d + lum;
    float minC = min(min(c.x, c.y), c.z);
    float maxC = max(max(c.x, c.y), c.z);
    if(minC < 0.0) return sLum + ((c - sLum) * sLum) / (sLum - minC);
    else if(maxC > 1.0) return sLum + ((c - sLum) * (1.0 - sLum)) / (maxC - sLum);
    else return c;
}





half3 blend( half3 s, half3 d, int mode )
{
    if(mode==0)    return darken(s,d);
    if(mode==1)    return multiply(s,d);
    if(mode==2)    return colorBurn(s,d);
    if(mode==3)    return linearBurn(s,d);
    if(mode==4)    return darkerColor(s,d);
    if(mode==5)    return lighten(s,d);
    if(mode==6)    return screen(s,d);
    if(mode==7)    return colorDodge(s,d);
    if(mode==8)    return linearDodge(s,d);
    if(mode==9)    return lighterColor(s,d);
    if(mode==10)    return overlay(s,d);
//    if(mode==11)    return softLight(s,d);
    if(mode==12)    return hardLight(s,d);
    if(mode==13)    return vividLight(s,d);
    if(mode==14)    return linearLight(s,d);
    if(mode==15)    return pinLight(s,d);
    if(mode==16)    return hardMix(s,d);
    if(mode==17)    return difference(s,d);
    if(mode==18)    return exclusion(s,d);
    if(mode==19)    return subtract(s,d);
    if(mode==20)    return divideBlendMode(s,d);
    if(mode==21)    return hue(s,d);
    if(mode==22)    return color(s,d);
    if(mode==23)    return saturation(s,d);
    if(mode==24)    return luminosity(s,d);

    return half3(0.0);
}
