half4 lutMix(half4 textureColor, float2 textureCoordinate, texture2d<half> lookupTexture, float intensity) {
//    float4 textureColor = inputImageTexture.sample(sam,textureCoordinate);
    float blueColor = textureColor.b * 63.0;

    float2 quad1;
    quad1.y = floor(floor(blueColor) / 8.0);
    quad1.x = floor(blueColor) - (quad1.y * 8.0);

    float2 quad2;
    quad2.y = floor(ceil(blueColor) / 8.0);
    quad2.x = ceil(blueColor) - (quad2.y * 8.0);

    float2 texPos1;
    texPos1.x = (quad1.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.r);
    texPos1.y = (quad1.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.g);

    float2 texPos2;
    texPos2.x = (quad2.x * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.r);
    texPos2.y = (quad2.y * 0.125) + 0.5/512.0 + ((0.125 - 1.0/512.0) * textureColor.g);

    half4 newColor1 = lookupTexture.sample(sam,texPos1);
    half4 newColor2 = lookupTexture.sample(sam,texPos2);
    half4 newColor = mix(newColor1, newColor2, fract(blueColor));

    return mix(textureColor, half4(newColor.rgb, textureColor.w), intensity);
}



float3 lutMixx(texture2d<float> inputImageTexture, float2 textureCoordinate, texture2d<float> lookupTexture, float intensity) {
     float2 tiles    = float2(8.0);
     float2 tileSize = float2(64.0);
     float4 texel = inputImageTexture.sample(sam, textureCoordinate);
     float index = texel.b * (tiles.x * tiles.y - 1.0);
     float index_min = min(62.0, floor(index));
     float index_max = index_min + 1.0;

     float2 tileIndex_min;
     tileIndex_min.y = floor(index_min / tiles.x);
     tileIndex_min.x = floor(index_min - tileIndex_min.y * tiles.x);
     float2 tileIndex_max;
     tileIndex_max.y = floor(index_max / tiles.x);
     tileIndex_max.x = floor(index_max - tileIndex_max.y * tiles.x);

     float2 tileUV = mix(0.5/tileSize, (tileSize-0.5)/tileSize, texel.rg);

     float2 tableUV_1 = tileIndex_min / tiles + tileUV / tiles;
     float2 tableUV_2 = tileIndex_max / tiles + tileUV / tiles;

     float3 lookUpColor_1 = lookupTexture.sample(samLut, tableUV_1).rgb;
     float3 lookUpColor_2 = lookupTexture.sample(samLut, tableUV_2).rgb;
     float3 lookUpColor   = mix(lookUpColor_1, lookUpColor_2, index-index_min);
//    lookUpColor = ( contrastMatrix(0.9) * float4(lookUpColor, 1.0)).rgb;
    return lookUpColor;
}

