float vingette(float2 uv, float2 res,float scale,float smooth){

    uv -= 0.5;
    float aspect = res.x/res.y;

    if(aspect < 1.0){
        uv.x *= res.x/res.y;
    }else{
        uv.y *= res.y/res.x;
    }
    float d = length(uv);
    d = smoothstep(scale + smooth,scale,d);


    return  d;
}


float2x2 rotate2d(float angle) {
    float s = sin(angle);
    float c = cos(angle);
    return float2x2(c, -s, s, c);
}

float3 radialBlur(texture2d<float> tex, float2 uv, float power,int samples) {



    float3 fragColor = float3(0.0);
    for (int i = 0; i < samples; i ++)
    {
        uv -= 0.5;
        uv *= rotate2d( power * float(i) );
        uv += 0.5;

        fragColor += pow(tex.sample(sam,uv).rgb, float3(2.2));
    }

    fragColor /= float(samples);
    fragColor = pow(fragColor, float3(1./2.2));
    return fragColor;;
}
float3 smoothskin(texture2d<float> tex, float2 uv, float r  , float steps){
     const float Y = 78.0;
     float2 res = float2(tex.get_width(),tex.get_height());
        float2 offset = float(1.0) / res.xy;




    // rgb Separate calculation
    float3 center_c = tex.sample(sam, uv).rgb;
    float3 sum_c = float3(0.0);
    for(int i = 0; i < 3; i++){
         float sum_i = 0.0;
        float norm = 0.0;
        for(float x = -r; x <= r; x+=steps){

                for(float y = -r; y <= r; y+=steps){
                    float2 uv2 = uv + float2(x * offset.x, y * offset.y);
                    float3 cur_c = tex.sample(sam, uv2).rgb;
//                    cur_c = highlight(cur_c, -0.5);
                float para = 1.0 - abs(center_c[i] - cur_c[i]) * 255.0 / (2.5 * Y);
                    sum_i += para * cur_c[i] * 255.0;
                norm += para;
                }
        }
        sum_c[i] = sum_i / norm;
    }

     return sum_c / 255.0;
}

half GetLuminance(half4 color)
{
    return 0.2126 * color.r + 0.7152 * color.g + 0.0722 * color.b;
}

half4 unsharpMask(float amount, float threshold,  half4 pixel,   half4 blurPixel)
{

    float lumDelta = abs(GetLuminance(pixel) - GetLuminance(blurPixel));

    if (lumDelta >= threshold)
        pixel = pixel + (pixel - blurPixel) * amount;

    return pixel;
}



float4 sharpen(float2 uv,texture2d<float> tex,float2 res,float2 nmp ){

    constexpr sampler samp(filter::linear,address::repeat);

    float2 nppd = 1.0/res.xy * 1.0;



    float4 c = float4(0.0);

    float4 tl = tex.sample(samp , uv + nppd * float2( -1.0, -1.0));
    float4 tc = tex.sample(samp , uv + nppd * float2(  0.0, -1.0));
    float4 tr = tex.sample(samp , uv + nppd * float2(  1.0, -1.0));
    float4 ml = tex.sample(samp , uv + nppd * float2( -1.0,  0.0));
    float4 mc = tex.sample(samp , uv + nppd * float2(  0.0,  0.0));
    float4 mr = tex.sample(samp , uv + nppd * float2(  1.0,  0.0));
    float4 bl = tex.sample(samp , uv + nppd * float2( -1.0,  1.0));
    float4 bc = tex.sample(samp , uv + nppd * float2(    0.0,  1.0));
    float4 br = tex.sample(samp , uv + nppd * float2(  1.0,  1.0));

    float4 dx = mc-ml;
    float4 dy = mc-tc;
    float4 d = (dx + dy) * 0.5;

    float sharpness = nmp.x * 10.0;
    c = mc - d * sharpness;

    return c;
}

float2 computeUV( float2 uv, float k, float kcube ){

    float2 t = uv - .5;
    float r2 = t.x * t.x + t.y * t.y;
    float f = 0.;

    if( kcube == 0.0){
        f = 1. + r2 * k;
    }else{
        f = 1. + r2 * ( k + kcube * sqrt( r2 ) );
    }

    float2 nUv = f * t + .5;
    //nUv.y = 1. - nUv.y;

    return nUv;

}


