half4 chromatic_aberration(float2 uv,texture2d<half>  image,float value) {
//              var inputBlur: CGFloat = 10
//                var inputFalloff: CGFloat = 0.2
//                var inputSamples: CGFloat = 10

              float sampleCount = 10.0;
    int sampleCountInt = int(floor(sampleCount));
    half4 accumulator = half4(0.0);
     float2 res = float2(image.get_width(),image.get_height());
              float2 size = res;
              float adaptiveValue = res.y/1080.;

//            adaptiveValue *= value;

              float blur = 3. * adaptiveValue;
              float start = 0.05 * adaptiveValue;

    float2 dc = uv*res;
    float normalisedValue = length(((dc / size) - 0.5) * 2.0);
    float strength = clamp((normalisedValue - start) * (1.0 / (1.0 - start)), 0.0, 1.0);
    strength *= value;
    float2 vector = normalize((dc - (size / 2.0)) / size);
    float2 velocity = vector * strength * blur;

    float2 redOffset = -vector * strength * (blur * 1.0);
    float2 greenOffset = -vector * strength * (blur * 1.5);
    float2 blueOffset = -vector * strength * (blur * 2.0);

    for (int i=0; i < sampleCountInt; i++) {
        accumulator.r += image.sample(sam, ( dc + redOffset) / res).r;
        redOffset -= velocity / sampleCount;

        accumulator.g += image.sample(sam,  ( dc + greenOffset) / res).g;
        greenOffset -= velocity / sampleCount;

        accumulator.b += image.sample(sam, (  dc + blueOffset) / res).b;
        blueOffset -= velocity / sampleCount;
    }

    return half4(half3(accumulator / half(sampleCountInt)), 1.0);
}


half4 overlay(half4 base, half4 blend) {
    half4 x = half4(2.0) * base * blend;
    half4 y = half4(1.0) - half4(2.0) * (half4(1.0) - base) * (half4(1.0) - blend);
    return mix(x, y, step(0.5, base));
}

half4 chromatic_aberrationLut(float2 uv,texture2d<half>  image,float value,    texture2d<half>  lut,float lutIntensity) {
//              var inputBlur: CGFloat = 10
//                var inputFalloff: CGFloat = 0.2
//                var inputSamples: CGFloat = 10

              float sampleCount = 10.0;
    int sampleCountInt = int(floor(sampleCount));
    half4 accumulator = half4(0.0);
     float2 res = float2(image.get_width(),image.get_height());
              float2 size = res;
              float adaptiveValue = res.y/1080.;

//            adaptiveValue *= value;

              float blur = 3. * adaptiveValue;
              float start = 0.05 * adaptiveValue;

    float2 dc = uv*res;
    float normalisedValue = length(((dc / size) - 0.5) * 2.0);
    float strength = clamp((normalisedValue - start) * (1.0 / (1.0 - start)), 0.0, 1.0);
    strength *= value;
    float2 vector = normalize((dc - (size / 2.0)) / size);
    float2 velocity = vector * strength * blur;

    float2 redOffset = -vector * strength * (blur * 1.0);
    float2 greenOffset = -vector * strength * (blur * 1.5);
    float2 blueOffset = -vector * strength * (blur * 2.0);

    for (int i=0; i < sampleCountInt; i++) {

        float2 uvR = ( dc + redOffset) / res;

        half4 r = image.sample(sam, uvR);
        r = lutMix(r, uvR, lut,  lutIntensity);
        accumulator.r += r.r;
        redOffset -= velocity / sampleCount;

        float2 uvG = ( dc + greenOffset) / res;

        half4 g = image.sample(sam, uvG);
        g = lutMix(g, uvG, lut,  lutIntensity);
        accumulator.g += g.g;
        greenOffset -= velocity / sampleCount;

        float2 uvB = ( dc + blueOffset) / res;
        half4 b = image.sample(sam, uvB);
        b = lutMix(b, uvB, lut,  lutIntensity);
        accumulator.b += b.b;
        blueOffset -= velocity / sampleCount;
    }

    return half4(half3(accumulator / half(sampleCountInt)), 1.0);
}



half4 applyEffects(int type,float strength, MetalUniform values,
                    texture2d<half>  sourceTexture,
                   half4 blured,
                   half4 halation,
                   texture2d<half>  lut,
                    float2 uv,
                   half4 camCol,bool lutAfter){


    //MARK: EFFECTS
    constexpr metal::sampler samNMain(metal::filter::linear,address::clamp_to_edge);


    //MARK: NONE
    if(type == 0){
        return camCol;
    }else if(type == 1){
        //MARK: UNSHARP MASK

        camCol = mix(camCol, unsharpMask(1.4,0.0, camCol, blured), strength);
//        camCol =  lutMix(camCol, uv, lut,  values.lutIntesity);
    }else if(type == 2){
        //MARK: FILM BLUR
        float adaptiveValue = sourceTexture.get_height()/1080.;
        camCol.rgb = blur(sourceTexture,uv,strength*2*adaptiveValue);
//        if(values.lutBefore == 1){
        if(!lutAfter){
            camCol =  lutMix(camCol, uv, lut,  values.lutIntesity);
        }
//        }

    }else if(type == 3){
        //MARK: white_halation

        camCol.rgb = screen(halation.rgb, camCol.rgb) * strength + camCol.rgb * (1.0 - strength);
        //MARK: red_halation
    }else if(type == 4){
//        camCol.rgb = halation.rgb;
        camCol.rgb = screen(halation.rgb*half3(1.0,0.0,0.0), camCol.rgb) * strength + camCol.rgb * (1.0 - strength);
        //            camCol /= 1.3
        //MARK:  SOFT
    }else if(type == 5){

        half4 colBefore = camCol;
        camCol += blured*0.5;
        camCol /= 1.5;
//        colBefore.r = 1.0;
        camCol = mix(colBefore,camCol, strength);

        //MARK: sharpen
    }else if(type == 6){
        half3 edge = clamp(( blured.rgb - camCol.rgb),0.,1.0);
        float a = edge.r + edge.g + edge.b;

        camCol.rgb /= 1 + half3(a)*strength;
        //MARK: chromatic_aberration
    }else if(type == 7){


//        if(values.lutBefore == 1){
        if(!lutAfter){
            camCol.rgb = chromatic_aberrationLut(uv, sourceTexture,strength ,lut,values.lutIntesity).rgb;
        }else{
            camCol.rgb = chromatic_aberration(uv, sourceTexture,strength ).rgb;
        }
//        }
    }else if(type == 8){
        if(!lutAfter){
            blured = lutMix(camCol, uv, lut,  values.lutIntesity);
        }
        return blured;
    }
    //MARK: EFFECTS
    return camCol;
}



