kernel void main_kernel(
                   texture2d<half, access::sample> input [[texture(0)]],
                   texture2d<half, access::write> output [[texture(1)]],
                        texture2d<half, access::sample> lut [[texture(2)]],
                        texture2d<half, access::sample> bluredTexture [[texture(3)]],
                        texture2d<half, access::sample> frameTexture [[texture(4)]],
                        texture2d<half, access::sample> halationTexture [[texture(5)]],
                        constant bool &photo [[buffer(7)]],
                        constant bool &lutAfter [[buffer(8)]],
                        constant EffectUniforms *effects [[buffer(11)]],
                        constant MetalUniform &values [[buffer(12)]],
                        constant OverlayUniforms *overlayBuffers [[buffer(13)]],
                        array<texture2d<half, access::sample>, 3> overlays [[texture(22)]],
                   uint2 gid [[thread_position_in_grid]])
{

    float2 res = float2(input.get_width(), input.get_height());

    float2 uv = float2(gid.x,gid.y) / res;


    float2 overlayUV = uv;
    if (photo){
        overlayUV = overlayUV.yx;
    }

    half4 camCol = input.sample(samNMain, uv,level(5));
    half4 frameCol = frameTexture.sample(samNMain, overlayUV);
    float2 cvUV = uv;
    cvUV.y = 1.0 - cvUV.y;
    half4 halation = halationTexture.sample(samNMain, cvUV);

    half4 blured = half4(0.0);
    blured = bluredTexture.sample(samNMain, uv);

    if(!lutAfter){
        camCol =  lutMix(camCol, uv, lut,  values.lutIntesity);
    }
    //MARK: EFFECTS
    for(int i = 0; i < values.filtersCount; i++){
        camCol = applyEffects(effects[i].type,effects[i].strength, values, input, blured, halation, lut, uv, camCol,lutAfter);
    }
    //MARK: EFFECTS

        for(int i = 0; i < values.overlaysCount; i++){

            half4 overlay = overlays[i].sample(samNMain, overlayUV);//*overlayBuffers[i].overlayStrength;

            camCol.rgb = blend( overlay.rgb/**overlay.a*/, camCol.rgb, overlayBuffers[i].overlayBlendMode) * overlayBuffers[i].overlayStrength + camCol.rgb * (1.0 - overlayBuffers[i].overlayStrength);

        }


//
    if(lutAfter){
        camCol =  lutMix(camCol, uv, lut,  values.lutIntesity);
    }
    if (gid.x >= output.get_width() || gid.y >= output.get_height()) {
           return;
       }
        output.write(camCol, gid);
}




