//// Downsampler
// Downsampler
fragment float4 frag_down(VertexOut fragmentIn [[stage_in]],
                           texture2d<float, access::sample> video [[texture(0)]])
{
    constexpr sampler sam(mag_filter::linear, min_filter::linear);


    float2 size = float2(video.get_width(),video.get_height());
    float texelSize = 1.0 / size.x;

    const float hscale = 1.25;
      const float dx = texelSize * hscale;

    float u0 = fragmentIn.textureCoorinates.x - dx * 5.0;
    float u1 = fragmentIn.textureCoorinates.x - dx * 3.0;
    float u2 = fragmentIn.textureCoorinates.x - dx * 1.0;
    float u3 = fragmentIn.textureCoorinates.x + dx * 1.0;
    float u4 = fragmentIn.textureCoorinates.x + dx * 3.0;
    float u5 = fragmentIn.textureCoorinates.x + dx * 5.0;

    float3 c0 = video.sample(sam,float2(u0, fragmentIn.textureCoorinates.y)).rgb;
    float3 c1 = video.sample(sam,float2(u1, fragmentIn.textureCoorinates.y)).rgb;
    float3 c2 = video.sample(sam,float2(u2, fragmentIn.textureCoorinates.y)).rgb;
    float3 c3 = video.sample(sam,float2(u3, fragmentIn.textureCoorinates.y)).rgb;
    float3 c4 = video.sample(sam,float2(u4, fragmentIn.textureCoorinates.y)).rgb;
    float3 c5 = video.sample(sam,float2(u5, fragmentIn.textureCoorinates.y)).rgb;

    float3 c = (c0 + c1 + c2 + c3 + c4 + c5) / 6.0;

    return float4(c, 1.0);
}


