fragment float4 halation_fragment(VertexOut fragmentIn [[stage_in]],
                      texture2d<float, access::sample> video [[texture(0)]],
                      texture2d<float, access::sample> halation [[texture(1)]],
                                  constant bool &orientation [[buffer(10)]]) {

     constexpr sampler sam(mag_filter::linear, min_filter::linear);
     float2 uv = fragmentIn.textureCoorinates;



    float2 hUV = float2(uv.x, 1.0 - uv.y);
    if (orientation){
        uv = float2(uv.x, 1.0 - uv.y);
        hUV = float2(hUV.x, 1.0 - hUV.y);
    }


    float3 halatioColor = float3(0.9,0.4,0.27);
    float3 halatioColorbright = float3(1.0,0.85,0.72);
    float halationMask = halation.sample(sam,  hUV).r*2.;
    float3 videoC = video.sample(sam,  uv).rgb;
    float3 color = videoC;
    color.gb /= 1.1;

    halatioColor = mix(halatioColor, halatioColorbright,halationMask/2.)*(halationMask*2.);
    halatioColor = saturation(halatioColor,2.0);
    color = float3(screen(half3(halatioColor*0.75),half3(color)));
     return float4(color,1.0);

}


float pizza(float2 p, float2 a, float2 b,float2 res){
    float2 pa = p-a;
    float2 ba = b-a;

    float t = clamp(dot(pa, ba)/dot(ba,ba)/2.,0.,1.0);
    float2 cv = pa - ba*t;
    // cv.y *= res.y/res.x;
    float d = length(cv);
    d = smoothstep(0.0,0.7,d);
    // if(distance(a, b)>2.){return 2.;}else{
    return ( d);
    // }
}


float3 rgbDisp(texture2d<float> tex, float2 uv, float r,float val){
//    float R = blur(tex,uv-0.01,r).r;
//    float G =  blur(tex,uv,r+.2).g;
    float R = tex.sample(sam,  uv-0.01).r;
    float G = tex.sample(sam,  uv+float2(0.01,-0.01)).g;
    float B = tex.sample(sam,  uv+0.01).b;
//    float R =  blur(tex,uv-0.01,r-.3).b;
//    float G =  blur(tex,uv+float2(0.01,-0.01),r-.3).b;
//    float B =  blur(tex,uv+0.01,r-.3).b;
    float3 col = tex.sample(sam,  uv).rgb;
    return mix(col,float3(R,G,B),val);
}



kernel void kaleida(
                   texture2d<float, access::sample> input [[texture(0)]],
                   texture2d<float, access::write> output [[texture(1)]],
                 texture2d<float, access::write> cvTexture [[texture(2)]],
                     constant float3 *fcBuffer [[buffer(8)]],
                     constant int &count [[buffer(7)]],
                   uint2 gid [[thread_position_in_grid]])
{
    float effectValue = 0.5;
     float4 fragColor = float4(0.0);
    float tt = 112.123;
    float2 res = float2(input.get_width(), input.get_height());
    float2 uv = float2(gid.x,gid.y) / res;

//    float3 maskCol = maskTex.sample(sam,  uv).rgb;
    float3 pc[4];
        pc[0] = float3(0.0,1.0,0.87);
        pc[1] = float3(0.367, 0.0, 1.0);
        pc[2] = float3(1.0,0.023,0.0);
        pc[3] = float3(1.0,0.8,0.0);
//      if (orientation){
//          uv = float2(uv.x, 1.0 - uv.y);
//      }
    float u_time =  pi/3.;
    float2 pUV = uv - 0.5;
    float2 u_resolution = res;
//    if(mirr){
//     uv.x = 1.-uv.x;
//    }
     constexpr sampler sam(filter::linear);



    float3 color = 0.0;//input.sample(sam, float2(uv.x,uv.y) ).rgb;
    float3 col = 0.0;//input.sample(sam, float2(uv.x,uv.y) ).rgb;//color;


    float rad = 0.9 ;
    float3 p0 = float3(rad * sin(u_time/1.2-pi/2.), rad * cos(u_time/1.2-pi/2.), 0.1);
    float3 p1 = float3(rad * sin(u_time/1.2+pi/2.), rad * cos(u_time/1.2+pi/2.), 0.1);
    float3 p2 = float3(rad * sin(u_time/1.2+pi), rad * cos(u_time/1.2+pi), 0.1);
    float3 p3 = float3(rad * sin(u_time/1.2-pi*2.0), rad * cos(u_time/1.2-pi*2.), 0.1);
    pUV*=4.0;
    float4 d = float4(0.0);
    float scale = 9.0;
    d.r += pizza(pUV/scale,p0.xy,float2(0.0),u_resolution.xy);
    d.g += pizza(pUV/scale,p1.xy,float2(0.0),u_resolution.xy);
    d.b += pizza(pUV/scale,p2.xy,float2(0.0),u_resolution.xy);
    d.a += pizza(pUV/scale,p3.xy,float2(0.0),u_resolution.xy);
    d.rgba = smoothstep(0.8,.5,d.rgba);

    float3 colTx = rgbDisp(input,uv - p0.xy/6.,1.0,mix(0.0,(1.0 - d.r)*2.,effectValue));//texture2D(u_texture_1,uv+0.5 - p0.xy/6.).rgba;
    col += colTx.rgb*d.r;
    colTx.rgb = rgbDisp(input,uv - p1.xy/6.,1.0,mix(0.0,(1.0 - d.g)*2.,effectValue));
    col += colTx.rgb*d.g;
    colTx.rgb = rgbDisp(input,uv - p2.xy/6.,1.0,mix(0.0,(1.0 - d.b)*2.,effectValue));
    col += colTx.rgb*d.b;
    colTx.rgb = rgbDisp(input,uv - p3.xy/6.,1.0,mix(0.0,(1.0 - d.a)*2.,effectValue));
    col += colTx.rgb*d.a;

    uv-=0.5 ;
    float centerShape = abs(uv.y);
   centerShape = smoothstep(0.3,.1,centerShape);
   centerShape *= smoothstep(0.3,.1,abs(uv.x));
    uv+=0.5;


    col *= 1.0 - centerShape;
    colTx.rgb =  rgbDisp(input,uv,1.0,1.0 - centerShape);
   col += colTx.rgb*centerShape;
//    col.rgb =  rgbDisp(input,uv,1.0);
    output.write(float4(col,1.0), gid);
//     return float4(col, 1.0);
}

