kernel void rgb_shift(
                   texture2d<float, access::sample> input [[texture(0)]],
                   texture2d<float, access::write> output [[texture(1)]],
                 texture2d<float, access::write> cvTexture [[texture(2)]],
                     constant float3 *fcBuffer [[buffer(8)]],
                     constant int &count [[buffer(7)]],
                   uint2 gid [[thread_position_in_grid]] ){

    float2 res = float2(input.get_width(), input.get_height());
    float2 uv = float2(gid.x, gid.y) / res;

    constexpr sampler sam(filter::linear);
    float shift = 0.002;
    float3 col = float3(0.0);
    uv /= 1.001;
    uv.x += shift;
    col.r = input.sample(sam, uv).r;
    uv.x -= shift;
    col.g = input.sample(sam, uv).g;
    uv.x -= shift;
    col.b = input.sample(sam, uv).b;

    output.write(float4(col, 1.0), gid);
}

