kernel void fisheye(
                   texture2d<float, access::sample> input [[texture(0)]],
                   texture2d<float, access::write> output [[texture(1)]],
                 texture2d<float, access::sample> cvTexture [[texture(2)]],
                     constant float3 *fcBuffer [[buffer(8)]],
                     constant int &count [[buffer(7)]],
                   uint2 gid [[thread_position_in_grid]] ){

    float2 res = float2(input.get_width(), input.get_height());
    float2 uv = float2(gid.x,gid.y) / res;

    float vignette = 1.0 - length(uv - float2(0.5)) * 2.0;

    constexpr sampler sam(filter::linear);

    float k = 1.1;
    float offset = 0.05;
//                       uv -= 0.5;
    float2 uvFisheye = uv;
                       uvFisheye -= 0.5;
                       uvFisheye /= 1.3;
                       uvFisheye += 0.5;
    float vign = vingette(uv, res, 0.45, 0.08);
                       offset *= 1.0 - vign + 0.1;
    float2 r = computeUV(uvFisheye, k + offset);
    float2 g = computeUV(uvFisheye, k);
    float2 b = computeUV(uvFisheye, k - offset);
//                       uv += 0.5;
//                       r += 0.5;
//                       g += 0.5;
//                       b += 0.5;


    float red = input.sample(sam, r).r;
    float green = input.sample(sam, g).g;
    float blue = input.sample(sam, b).b;

    float3 col = float3(red, green, blue) * vign;

    output.write(float4(col, 1.0), gid);
}



