float3 bokehSampler(texture2d<float> tex, float2 uv, float radius, float amount, float2 res)
{
    float3 acc = float3(0.0);
    float3 div = float3(0.0);
    float2 pixel = float2(res.y/res.x, 1.0) * radius * .006;
    float r = 1.0;
    for (float j = 0.0; j < 30.; j += 0.25)
    {
           float2 shiftVal =  shift(j, r);
        float3 col = tex.sample(sam,  uv + pixel * shiftVal).rgb;
        float3 bokeh = float3(5.0) + pow(col, float3(9.0)) * amount;
        acc += col * bokeh;
        div += bokeh;
    }
    return acc / div;
}


kernel void bokeh( texture2d<float, access::sample> input [[texture(0)]],
