    float focusShape = abs(uv.y-0.5);

    focusShape = smoothstep(0.0,0.4,focusShape);
    float4 color = float4(bokehSampler(input, uv, focusShape, a,res), 1.0);
    // color = float4(focusShape);
    output.write(color, gid);

}

#define pi 3.14159265358979323846

float3 drawGoldenSparkle(float2 uv, float size, float2 position) {
    float3 color = float3(0.0);
    float2 p = uv - position;
    p /= (size) + 0.01 ;
    float2 pos = float2(0.5);

    float an = mod(atan2(p.y, p.x) + 6.2831 / 3.0, 6.2831 / 6.0) - 6.2831 / 3.0;
    p = float2(cos(an), sin(an)) * length(p);

    float rad = 256.;//mix(150.0,10.,size);
//    float rad = mix(256.0,56.,size);
    float br = 250.0 * pow(1.0 / max(10.0, rad * (sqrt(abs(p.x)) + sqrt(abs(p.y))) + 0.9), 2.5);
    color = mix(float3(br, 0.4 * br, 0.0), float3(1.0), br);
    return color;
}
float3 starlightSparkle( float2 uv, float size, float2 position) {

            float3 color = float3(0.0);
            float2 p = uv - position;
            p /= (size) + 0.01 ;
            float2 pos = float2(0.5);

            float an = mod(atan2(p.y, p.x) + 6.2831 / 3.0, 6.2831 / 8.0) - 6.2831 / 3.0;
            p = float2(cos(an), sin(an)) * length(p);

            float rad = 256.;//mix(150.0,10.,size);
        //    float rad = mix(256.0,56.,size);
            float br = 250.0 * pow(1.0 / max(10.0, rad * (sqrt(abs(p.x)) + sqrt(abs(p.y))) + 0.9), 2.5);
            color = mix(float3(br, 0.4 * br, 0.0), float3(1.0), br);
             float glow = length(p);
             glow = smoothstep(0.025, 0.0, glow)/4.;
             color += glow;
            return color;
}
float3 drawflare(float2 p, float intensity,  float blur, float glow)
{


    p = rotateUV(p, pi / 4.);  // rotate sparkle here


    p *= 2.0;//sparkle scale

    float lingrad = distance(float2(0.), p); //linear radial gradient
    float expgrad = 1. / exp(lingrad ); //exponential radial gradient
    float3 colgrad = float3(
                            hsv2rgb(
                                    half3( fract( (expgrad * 8.) ), pow(1.-abs(expgrad*2.-1.), 0.45), 210.0 * expgrad * intensity)
                                    )
                            ); //rainbow spectrum effect


    float blades = length(p * sin(2.0 * atan2(p.y, p.x))); //draw 6 blades

    float comp = pow(1. - saturateF( blades ), 13. - (blur*12.)); // sharpen effect
    comp += saturateF((expgrad-0.9)) * 3.;
    comp = pow(comp * expgrad, 1 + (1.-intensity) * 5.); // compose and sharpen effect
    float3 col = float3(comp) * colgrad;
    float d = length(p);
    d = smoothstep(intensity/1. * 3.,intensity/10. * 2. , d)/2. * glow;
    col += d; // adding a smooth glow effect
    return  col;
}





