            d += drawGoldenSparkle((uv-sparklePos),size*1000.,float2(0.));
        }
    }
    float4 camCol = input.sample(sam, uv);
     output.write(float4(camCol.rgb + d,1.0), gid);
}

kernel void starlight(
                   texture2d<float, access::sample> input [[texture(0)]],
                   texture2d<float, access::write> output [[texture(1)]],

                     constant float3 *cvBuffer [[buffer(8)]],
                     constant int &count [[buffer(7)]],
                     constant bool &photo [[buffer(11)]],
                   uint2 gid [[thread_position_in_grid]] )
{

     float2 res = float2(input.get_width(), input.get_height());

     float2 uv = float2(gid.x,gid.y) / res;

    float3 d = float3(0.0);

    for(int i = 0; i < count; i+=1 ){
        float spsz = cvBuffer[i].z;
        spsz = smoothstep(0.0, 1.0, spsz);
        float sizeOp = cvBuffer[i].z;// (spsz * 1.5 );
        float size =  cvBuffer[i].z;
        float2 sparklePos = cvBuffer[i].xy;

//
//        if(photo){
//            float2 overlayres = float2(480.0, 640.0);
//            float2 scale = res / overlayres;
//
//            sparklePos = sparklePos / scale;
//            sparklePos = sparklePos.yx;
//
//        }
        if (!photo){
            sparklePos.y = 1.0 - sparklePos.y;
        }

        if(cvBuffer[i].x != float(0.0)){
//            d += drawflare((uv - sparklePos )*8, size*2.0, 0.01, values.u_time/2., i, values.res, 0.5,0.5)/( float(int(sizeOp * 10.))/10. + 1. );
            d += starlightSparkle((uv-sparklePos),size*1000.,float2(0.));
        }
    }
    float4 camCol = input.sample(sam, uv);
     output.write(float4(camCol.rgb + d,1.0), gid);
}


kernel void none(
                   texture2d<float, access::sample> input [[texture(0)]],
                   texture2d<float, access::write> output [[texture(1)]],
                 texture2d<float, access::write> cvTexture [[texture(2)]],

                   uint2 gid [[thread_position_in_grid]] )
{

     float2 res = float2(input.get_width(), input.get_height());

     float2 uv = float2(gid.x,gid.y) / res;


     float4 camCol = input.sample(sam, uv);
    camCol.r = 1.0;
     output.write((camCol), gid);
}
